JOIN F23_S001_T1_STORE S ON PF.StoreID = S.Store_ID
GROUP BY
S.Store_ID,
TO_CHAR(PF.PurchaseDate, 'YYYY-MM')
ORDER BY
S.Store_ID,
TO_CHAR(PF.PurchaseDate, 'YYYY-MM');

