-- Top 10 Best Selling Books for Each Store
WITH RankedBooks AS (
  SELECT s.Store_ID, b.BookName, COUNT(*) as TotalSold, ROW_NUMBER() OVER (PARTITION BY s.Store_ID ORDER BY COUNT(*) DESC) AS BookRank
  FROM F23_S001_T1_SOLD_BY s
  JOIN F23_S001_T1_BOOK b ON s.ISBN = b.ISBN
  GROUP BY s.Store_ID, b.BookName
)
SELECT Store_ID, BookName, TotalSold
FROM RankedBooks
WHERE BookRank <= 10;

