-- For each store, evaluate the days (Monday - Sunday) during which the store sold more than 10 copies. This aids in optimizing staffing and store hours.
WITH StoreSales AS (
SELECT
 		s.Store_ID,
 		TO_CHAR(pf.PurchaseDate, 'Dy') AS DayOfWeek,
 		COUNT(*) AS CopiesSold
  	FROM
    		F23_S001_T1_PURCHASES_FROM pf
    		JOIN F23_S001_T1_STORE s ON pf.StoreID = s.Store_ID
  	GROUP BY
    		s.Store_ID, TO_CHAR(pf.PurchaseDate, 'Dy')
)
SELECT
  	Store_ID,
  	DayOfWeek,
  	SUM(CopiesSold) AS TotalCopiesSold
FROM
  	StoreSales
WHERE
  	CopiesSold > 10
GROUP BY
  	Store_ID, DayOfWeek
ORDER BY
  	Store_ID, DayOfWeek;

