-- Which age group amongst children (0, 12), teens (13, 19), adults (20, 50) and senior (above 50) makes the maximum purchases amongst that age group.
SELECT AgeGroup, COUNT(*) as PurchaseCount
FROM (
  SELECT CustomerID, 
    CASE
      WHEN AGE <= 12 THEN 'Children'
      WHEN AGE BETWEEN 13 AND 19 THEN 'Teens'
      WHEN AGE BETWEEN 20 AND 50 THEN 'Adults'
      ELSE 'Senior'
    END AS AgeGroup
  FROM (
    SELECT CustomerID, FLOOR((SYSDATE - DateOfBirth) / 365) as AGE 
    FROM F23_S001_T1_CUSTOMER
  )
) CustomerAge
JOIN F23_S001_T1_BUYS ON CustomerAge.CustomerID = F23_S001_T1_BUYS.CustomerID
GROUP BY AgeGroup
ORDER BY PurchaseCount DESC
FETCH FIRST 1 ROWS ONLY;

