-- This query will return the total amount spent by each customer and the average amount spent by all customers on their purchases from each store.
SELECT 
    PF.CustomerID, 
    PF.StoreID, 
    SUM(PF.Amount) OVER (PARTITION BY PF.CustomerID, PF.StoreID) as AvgCustomer,
    AVG(PF.Amount) OVER (PARTITION BY PF.StoreID) as AvgSpentPerStore
FROM 
    F23_S001_T1_PURCHASES_FROM PF
JOIN 
    F23_S001_T1_CUSTOMER C ON PF.CustomerID = C.CustomerID
JOIN 
    F23_S001_T1_STORE S ON PF.StoreID = S.Store_ID;


